import React from "react";
import BookShelf from "./BookShelf";


function MainShelf({ books, updateShelf, book,search, bookFound }) {
  
  const filter = (book) => shelf => book.shelf(b => b.shelf === shelf)
  const filterBy = filter(books)
  
  const wantToRead = filterBy('wantToRead')
  const currentlyReading = filterBy('currentlyReading')
  const read = filterBy('read')

  return (
    <div>
      <BookShelf
        shelfName="Currently Reading"
        books={currentlyReading}
        updateShelf={updateShelf}
        book={book}
        search={search}
        bookFound={bookFound}
      />
      <BookShelf
        shelfName="Want To Read"
        books={wantToRead}
        updateShelf={updateShelf}
        
      />

      <BookShelf shelfName="Read" books={read} updateShelf={updateShelf} />
    </div>
  );
}

export default MainShelf;
